import rightArrow from "./btnSlider/right-arrow.svg"
import leftArrow from "./btnSlider/left-arrow.svg"
import style from "./home.module.scss"
import clsx from "clsx"

export default function BtnSlider({ direction}) {
  return (
    <button className={direction === "next" ? clsx(style.btnNext, style.btnSlider) : clsx(style.btnPrev, style.btnSlider)}>
      <img className={clsx(style.imgSlider)} src={direction === "next" ? rightArrow : leftArrow } />
    </button>
  )
}
