import { useContext, useEffect, useState } from 'react'
import { ThemeContext } from '../../../App'
import style from './cart.module.scss'
import clsx from 'clsx'


export default function CartItem() {
  const [item, setItem] = useState(null)
  const [data, cart] = useContext(ThemeContext)
  if(cart){
    return (
      <ul className={clsx(style.cartItems, 'row')}>
        <li className={clsx(style.cartItem, 'col', 'l-2')}>
          <img className={clsx(style.cartItemImg)} src={data[0].image} />
        </li>
        <li className={clsx(style.cartItem, 'col', 'l-2')}>{data[0].title}</li>
        <li className={clsx(style.cartItem, 'col', 'l-4')}>{data[0].description}</li>
        <li className={clsx(style.cartItem, 'col', 'l-2')}>{data[0].price}</li>
        <li className={clsx(style.cartItem, 'col', 'l-2')}>
          <button className={clsx(style.cartItemRemove)}>x</button>
        </li>
      </ul>
    )
  }
  return (
    <h1>empty</h1>
  )
}
