import { useState } from "react"

const [collection , setCollection] = useState(null)
const [product, setProduct] = useState(null)
fetch("https://fakestoreapi.com/products")
  .then((res) => res.json())
  .then((json) => {
    setProduct(json)
    setCollection(new Set(json.map(item => item.category)))
  });
const page = ['home', 'product', 'cart', 'blog', 'contact', '404']
const color = ['red', 'white', 'blue', 'black', 'pink']
const popularTags = ['black', 'blue', 'browm', 'l', 'm', 'ml', 'pink', 'sky', 'white', 'x', 'xl']
export {page, color, popularTags, collection}
export default product
